          <select class="dn-select" data-brush-select aria-label="Color mini plancha">
            {%- for v in brush_product.variants -%}
              <option
                value="{{ v.id }}"
                {% if v.id == brush_variant.id %}selected{% endif %}
                {% if v.available == false %}disabled{% endif %}
              >
                {{ v.title }}
              </option>
            {%- endfor -%}
          </select>
        </div>
      </div>

      <!-- Linea B -->
      <div class="dn-line">
        <div class="dn-line__head">
          <span class="dn-check" aria-hidden="true">‚úì</span>
          <div class="dn-line__name">Sat√©n Aura sin frizz</div>
          <div class="dn-line__price">
            {%- if hat_product and hat_variant -%}
              <span class="dn-now">{{ hat_variant.price | money }}</span>
              {%- if hat_variant.compare_at_price and hat_variant.compare_at_price > hat_variant.price -%}
                <span class="dn-was">{{ hat_variant.compare_at_price | money }}</span>
              {%- endif -%}
            {%- else -%}
              <span class="dn-now">‚Äî</span>
            {%- endif -%}
          </div>
        </div>

        <div class="dn-line__ctrl">
          {%- if hat_product and hat_product.variants.size > 0 -%}
            <select class="dn-select" data-hat-select aria-label="Color gorro">
              {%- for v in hat_product.variants -%}
                <option
                  value="{{ v.id }}"
                  data-title="{{ v.title | escape }}"
                  {% if v.id == hat_variant.id %}selected{% endif %}
                  {% if v.available == false %}disabled{% endif %}
                >
                  {{ v.title }}
                </option>
              {%- endfor -%}
            </select>
          {%- else -%}
            <div class="dn-missing">
              No se encontr√≥ el producto del gorro. Revisa el handle en el c√≥digo.
            </div>
          {%- endif -%}
        </div>
      </div>
    </div>

    <!-- Totales -->
    <div class="dn-total">
      <div class="dn-total__label">Total:</div>
      <div class="dn-total__val">
        <span class="dn-total__now" data-total-now>‚Äî</span>
        <span class="dn-total__was" data-total-was style="display:none;">‚Äî</span>
      </div>
    </div>

    <button class="dn-btn" type="button" data-dn-add>
      A√±adir combo al carrito
    </button>

    <div class="dn-status" data-dn-status aria-live="polite"></div>
  </div>
</div>

<style>
