  /* Lines */
  .dn-lines{
    display:grid;
    gap: 12px;
  }

  .dn-line{
    border: 2px solid rgba(139,92,255,.22);
    border-radius: 18px;
    padding: 14px 14px;
    background: linear-gradient(180deg, var(--dn-lavender), #fff);
