    text-decoration: line-through;
    font-weight: 800;
  }

  /* Select estilo ‚Äúpill‚Äù lila */
  .dn-select{
    width:100%;
    border-radius: 14px;
    padding: 13px 14px;
    border: 2px solid rgba(139,92,255,.25);
    background: #fff;
    font-weight: 900;
    font-size: 14.5px;
    box-shadow: 0 10px 26px rgba(0,0,0,.05);
    transition: transform .15s ease, box-shadow .2s ease, border-color .2s ease;
  }
  .dn-select:focus{
    outline:none;
    border-color: rgba(111,44,243,.55);
    box-shadow: 0 18px 45px rgba(111,44,243,.12), 0 18px 45px rgba(0,0,0,.08);
    transform: translateY(-1px);
  }
  .dn-select option[disabled]{ color: rgba(0,0,0,.35); }

  /* Total */
  .dn-total{
    display:flex;
    align-items:baseline;
    justify-content:space-between;
    gap: 12px;
    padding: 12px 2px 12px;
