  .dn-total__label{
    font-weight: 950;
    font-size: 22px;
    letter-spacing: -.2px;
  }
  .dn-total__now{
    font-weight: 950;
    font-size: 36px;
    letter-spacing: -.7px;
    color: var(--dn-purple);
  }
  .dn-total__was{
    margin-left: 10px;
    font-weight: 900;
    color: rgba(0,0,0,.40);
    text-decoration: line-through;
    font-size: 18px;
  }

  /* Bot√≥n rosado-lavanda premium (similar a tu referencia) */
  .dn-btn{
    width:100%;
    border:none;
    border-radius: 18px;
    padding: 18px 16px;
    font-weight: 950;
    font-size: 16px;
    letter-spacing: .4px;
    cursor:pointer;
    background: linear-gradient(180deg, #FFD6E6, #F2D8FF);
    color:#0b0b0b;
    box-shadow: 0 26px 78px rgba(139,92,255,.18), 0 22px 60px rgba(255,214,230,.45);
    transition: transform .15s ease, box-shadow .2s ease, opacity .2s ease;
  }
  .dn-btn:hover{
    transform: translateY(-2px);
    box-shadow: 0 34px 95px rgba(139,92,255,.22), 0 28px 80px rgba(255,214,230,.55);
  }
  .dn-btn:disabled{
    opacity:.6;
    cursor:not-allowed;
    transform:none;
  }

  .dn-status{
    margin-top: 10px;
    font-size: 13.5px;
    color: rgba(17,17,17,.70);
    min-height: 18px;
  }
  .dn-status strong{ color:#111; }

  .dn-missing{
    padding: 12px 12px;
    border-radius: 12px;
    background: rgba(139,92,255,.08);
    border: 1px solid rgba(139,92,255,.18);
