  @media (prefers-reduced-motion: reduce){
    .dn-hat-img--b{ animation:none !important; opacity:0 !important; }
    .dn-btn, .dn-select{ transition:none !important; }
  }
</style>

<script>
  (function(){
    const root = document.querySelector('[data-dn-combo]');
    if(!root) return;

    const brushSelect = root.querySelector('[data-brush-select]');
    const hatSelect   = root.querySelector('[data-hat-select]');
    const hatImgA     = root.querySelector('.dn-hat-img--a');
    const statusEl    = root.querySelector('[data-dn-status]');
    const addBtn      = root.querySelector('[data-dn-add]');

    const totalNowEl  = root.querySelector('[data-total-now]');
    const totalWasEl  = root.querySelector('[data-total-was]');

    const BRUSH_PRICE_MAP = {
      {% for v in brush_product.variants %}
        "{{ v.id }}": { price: {{ v.price | default: 0 }}, compare_at: {{ v.compare_at_price | default: 0 }} }{% unless forloop.last %},{% endunless %}
      {% endfor %}
    };

    const HAT_PRICE_MAP = {
      {% if hat_product %}
        {% for v in hat_product.variants %}
          "{{ v.id }}": { price: {{ v.price | default: 0 }}, compare_at: {{ v.compare_at_price | default: 0 }} }{% unless forloop.last %},{% endunless %}
        {% endfor %}
      {% endif %}
    };

    const DN_HAT_COLOR_IMAGES = {
      white: "https://cdn.shopify.com/s/files/1/0806/1409/7136/files/foto_del_gorro_blanco.png?v=1767022073",
      cream: "https://cdn.shopify.com/s/files/1/0806/1409/7136/files/foto_del_gorro_blanco.png?v=1767022073",
      beige: "https://cdn.shopify.com/s/files/1/0806/1409/7136/files/foto_del_gorro_blanco.png?v=1767022073",

      black: "https://cdn.shopify.com/s/files/1/0806/1409/7136/files/1_c91faf08-0626-4312-9833-bb00a1a15e7e.png?v=1767023823",
      red: "https://cdn.shopify.com/s/files/1/0806/1409/7136/files/2_0baf2436-4c0e-49ef-8e9e-0ea2ea265ea3.png?v=1767023831",

      blue: "https://cdn.shopify.com/s/files/1/0806/1409/7136/files/3.png?v=1767023848",
      navyblue: "https://cdn.shopify.com/s/files/1/0806/1409/7136/files/3.png?v=1767023848",

      gold: "https://cdn.shopify.com/s/files/1/0806/1409/7136/files/4.png?v=1767023858",
      khaki: "https://cdn.shopify.com/s/files/1/0806/1409/7136/files/4.png?v=1767023858",
      caramel: "https://cdn.shopify.com/s/files/1/0806/1409/7136/files/4.png?v=1767023858",

      fullwhite: "https://cdn.shopify.com/s/files/1/0806/1409/7136/files/5.png?v=1767023862"
    };

    function dnNormalize(raw){
      if(!raw) return "";
      let s = String(raw).trim();
      s = s.replace(/^\d+/, "");
      s = s.replace(/\s+/g, "").replace(/[-_]/g, "");
      return s.toLowerCase();
    }

    function dnGetHatKeyFromVariantText(txt){
      let key = dnNormalize(txt);

      if(key === "1black") key = "black";
      if(key === "2white") key = "white";
      if(key === "darkgray" || key === "3darkgray") key = "black";
      if(key === "winered" || key === "8winered" || key === "rosered" || key === "6rosered") key = "red";
      if(key === "10navyblue") key = "navyblue";
      if(key === "4khaki") key = "khaki";
      if(key === "5caramel") key = "caramel";

      if(key === "blanco") key = "white";
      if(key === "negro") key = "black";
      if(key === "rojo") key = "red";
      if(key === "azul") key = "blue";
      if(key === "dorado") key = "gold";

      return key;
    }

    function updateHatImage(){
      if(!hatSelect || !hatImgA) return;
      const opt = hatSelect.options[hatSelect.selectedIndex];
      const label = (opt && (opt.getAttribute('data-title') || opt.textContent)) || hatSelect.value;
      const key = dnGetHatKeyFromVariantText(label);
      const url = DN_HAT_COLOR_IMAGES[key];
      if(url) hatImgA.src = url;
    }

    function formatMoneyFromShopifyCents(cents){
      const n = (Number(cents) || 0) / 100;
      return new Intl.NumberFormat(undefined, {
        style: 'currency',
        currency: '{{ cart.currency.iso_code | default: "USD" }}'
      }).format(n);
    }

    function updateTotal(){
      const brushId = brushSelect ? brushSelect.value : null;
      const hatId   = hatSelect ? hatSelect.value : null;

      const b = brushId && BRUSH_PRICE_MAP[brushId] ? BRUSH_PRICE_MAP[brushId] : {price:0, compare_at:0};
      const h = hatId && HAT_PRICE_MAP[hatId] ? HAT_PRICE_MAP[hatId] : {price:0, compare_at:0};

      const now = (b.price || 0) + (h.price || 0);
      const was = (Math.max(b.compare_at || 0, b.price || 0)) + (Math.max(h.compare_at || 0, h.price || 0));

      totalNowEl.textContent = formatMoneyFromShopifyCents(now);

      if(was > now){
        totalWasEl.style.display = '';
        totalWasEl.textContent = formatMoneyFromShopifyCents(was);
      }else{
        totalWasEl.style.display = 'none';
        totalWasEl.textContent = '';
      }
    }

    async function addComboToCart(){
      try{
        statusEl.textContent = '';
        addBtn.disabled = true;
        addBtn.textContent = 'Agregando...';

        const brushId = brushSelect ? brushSelect.value : null;
        const hatId   = hatSelect ? hatSelect.value : null;

        if(!brushId || !hatId){
          statusEl.innerHTML = 'Selecciona los <strong>dos colores</strong> para agregar el combo.';
          addBtn.disabled = false;
          addBtn.textContent = 'A√±adir combo al carrito';
          return;
        }

        const payload = {
          items: [
            { id: Number(brushId), quantity: 1 },
            { id: Number(hatId), quantity: 1 }
          ]
        };

        const res = await fetch('/cart/add.js', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json', 'Accept': 'application/json' },
          body: JSON.stringify(payload)
        });

        if(!res.ok){
          const err = await res.json().catch(()=>null);
          const msg = (err && (err.description || err.message)) ? (err.description || err.message) : 'No se pudo agregar el combo.';
          statusEl.innerHTML = `<strong>Error:</strong> ${msg}`;
          addBtn.disabled = false;
          addBtn.textContent = 'A√±adir combo al carrito';
          return;
        }

        statusEl.innerHTML = 'Listo. <strong>Combo agregado</strong> al carrito.';
        addBtn.textContent = 'A√±adido';

        setTimeout(()=>{
          addBtn.disabled = false;
          addBtn.textContent = 'A√±adir combo al carrito';
        }, 900);

      }catch(e){
        statusEl.innerHTML = '<strong>Error:</strong> No se pudo agregar el combo.';
        addBtn.disabled = false;
        addBtn.textContent = 'A√±adir combo al carrito';
      }
    }

    function cleanHatOptionLabels(){
      if(!hatSelect) return;
      [...hatSelect.options].forEach(opt=>{
        const raw = opt.textContent || '';
        const cleaned = raw.replace(/^\s*\d+/, '').trim();
        opt.textContent = cleaned;
      });
    }

    if(hatSelect){
      cleanHatOptionLabels();
      hatSelect.addEventListener('change', ()=>{ updateHatImage(); updateTotal(); });
    }
    if(brushSelect){
      brushSelect.addEventListener('change', updateTotal);
    }
    if(addBtn){
      addBtn.addEventListener('click', addComboToCart);
    }

    updateHatImage();
    updateTotal();
  })();
</script>
